def letras_comun(tex1:str, tex2:str):
    cont=0
    
    for i in tex1:
        aux=0
        for j in tex2:
            aux+=1
            if i == j: 
                cont+=1
                tex2=tex2.replace(tex2[aux-1], "", 1)
                break
    return cont

def palabras_comun(tex1:str, tex2:str):
    cont=0
    atex1=tex1.split()
    atex2=tex2.split()

    
    for i in atex1:
        for j in atex2:
            if i == j: 
                cont+=1
                atex2.remove(i)
                break
    return cont


nomSim=[]
for i in nombres:
    nom_Cont=[]
    for j in nombres:
        if j == i:
            nom_Cont.append(0)
            continue
        else:
            nom_Cont.append(palabras_comun(i, j))
    nomSim.append([i, nombres[nom_Cont.index(max(nom_Cont))], max(nom_Cont), ((max(nom_Cont)*100)/len(i))])
